DROP TRIGGER IF EXISTS `before_insert_user`;
delimiter ;;
CREATE TRIGGER `before_insert_user` BEFORE INSERT ON `user` FOR EACH ROW BEGIN
	DECLARE num int;
	DECLARE user_type int;
	IF new.user_code is NULL or new.user_code="" THEN
		IF new.role_type in (3,7) THEN
			SET user_type = 1;
		ELSEIF new.role_type=0 THEN
			SET user_type=4;
		ELSEIF new.role_type=10 THEN
			SET user_type=3;
		ELSE
			SET user_type=2;
		END IF;
		SET new.user_code = if(new.user_code is null or new.user_code="",REPLACE(uuid(),"-",""),new.user_code);
		SET new.user_type=user_type;
		zh_name,nickname,source,active,user_type,status,fk_dept_id,create_time,update_time) VALUES 
		(new.user_code,new.username,new.hash,"MD5","",new.user_no,new.email,new.mobile,new.zh_name,new.fullname,new.source,new.active,new.user_type,new.delete_flag,new.fk_dept_id
		,new.create_time,new.update_time);
	END IF;
END
;;
delimiter ;

