DROP TRIGGER IF EXISTS `before_update_user`;
delimiter ;;
CREATE TRIGGER `before_update_user` BEFORE UPDATE ON `user` FOR EACH ROW BEGIN
	DECLARE s1 VARCHAR(40)character set utf8;
	IF new.hash<>old.hash AND new.user_code is not null AND new.user_code!='' THEN
		select `password` into s1 from bdm_auth_user where user_code=new.user_code;
		IF s1 is not NULL AND s1!=new.hash THEN
			update bdm_auth_user set `password`=new.hash where user_code=new.user_code;
		END IF;
	END IF;
END
;;
delimiter ;

